    fun httpDownload(
        httpUrl: String?,
        saveFile: String?
    ): Boolean { // 1.下载网络文件
        var byteRead: Int
        val url: URL
        try {
            url = URL(httpUrl)
        } catch (e1: MalformedURLException) {
            e1.printStackTrace()
            return false
        }
        return try { //2.获取链接
            val conn: URLConnection = url.openConnection()
            //3.输入流
            val inStream: InputStream = conn.getInputStream()
            //3.写入文件
            val fs = FileOutputStream(saveFile)
            val buffer = ByteArray(1024)
            while (inStream.read(buffer).also { byteRead = it } != -1) {
                fs.write(buffer, 0, byteRead)
            }
            inStream.close()
            fs.close()
            true
        } catch (e: FileNotFoundException) {
            e.printStackTrace()
            false
        } catch (e: IOException) {
            e.printStackTrace()
            false
        }
    }






