    fun sss(htmlStr:String):MutableMap<String,String>{

            var  pics = mutableMapOf<String,String>()
             var regEx_video="<video.*poster\\s*=\\s*(.*?)[^>]*?src\\s*=\\s*(.*?)[^>]*?>";
            var p = Pattern.compile(regEx_video,Pattern.CASE_INSENSITIVE);
            var m = p.matcher(htmlStr);
            var video="";
            var map = mutableMapOf<String,String>();
            while (m.find()) {
                video=m.group();
                var mPoster = Pattern.compile("poster\\s*=\\s*\"?(.*?)(\"|>|\\s+)").matcher(video);
                var mSrc = Pattern.compile("src\\s*=\\s*\"?(.*?)(\"|>|\\s+)").matcher(video);
                var poster ="";
                var src="";
                while (mPoster.find()){
                    poster=mPoster.group(1);
                }
                while (mSrc.find()){
                    src=mSrc.group(1);
                }
                map.put("poster", poster);
                map.put("src", src);
            }
            return map;


